function parseLargeTextData() {
  // Method 1: Extract name (after code and before phone number)
  // Pattern: TB[numbers] [space] [name] [phone] [rest]
  const namePattern = /TB\d+\s+(.+?)\s+0\d{9,10}/;
  const nameMatch = largeTextValue.match(namePattern);
  if (nameMatch) {
    textbox1Value = nameMatch[1].trim();
  }

  // Method 2: Extract address (after "ĐC:" or "Địa chỉ:")
  const addressPattern = /(?:ĐC:|Địa chỉ:)\s*(.+?)(?:\s+\w+\s+\d+|$)/i;
  const addressMatch = largeTextValue.match(addressPattern);
  if (addressMatch) {
    textbox2Value = addressMatch[1].trim();
  }
}
