````javascript path=index.html mode=EXCERPT
function parseLargeTextData() {
  // Method 1: Extract everything from start to "tắm" for textbox1
  const tamIndex = largeTextValue.indexOf('tắm');
  if (tamIndex !== -1) {
    textbox1Value = largeTextValue.substring(0, tamIndex).trim();
  } else {
    // If "tắm" not found, use the whole text
    textbox1Value = largeTextValue.trim();
  }

  // Method 2: Extract address (after "ĐC:" or "Địa chỉ:") for textbox2
  const addressPattern = /(?:ĐC:|Địa chỉ:)\s*(.+?)(?:\s+tắm|$)/i;
  const addressMatch = largeTextValue.match(addressPattern);
  if (addressMatch) {
    textbox2Value = addressMatch[1].trim();
  }
}
````

