<!-- Summary table -->
<h3 style="margin-top: 30px;">Thống kê theo Second Text</h3>
<table id="summaryTable">
  <thead>
    <tr>
      <th>Second Text</th>
      <th>Tổng Number</th>
      <th>Số lần</th>
    </tr>
  </thead>
  <tbody>
