   329	    function updateSummaryTable() {
   330	      const dataTable = document.querySelector('#dataTable tbody');
   331	      const summaryTable = document.querySelector('#summaryTable tbody');
   332	      const rows = dataTable.querySelectorAll('tr:not([style*="display: none"])');
   333	
   334	      // Clear summary table
   335	      summaryTable.innerHTML = '';
   336	
   337	      // Group data by second text
   338	      const groupedData = {};
   339	
   340	      rows.forEach(row => {
   341	        const cells = row.querySelectorAll('td');
   342	        if (cells.length >= 5) {
   343	          const secondText = cells[4].textContent.trim(); // Second Text column (at index 4)
   344	          const number = parseInt(cells[2].textContent) || 0; // Number column (now at index 2)
   345	
   346	          if (secondText) {
   347	            if (!groupedData[secondText]) {
   348	              groupedData[secondText] = {
   349	                total: 0,
   350	                count: 0
   351	              };
   352	            }
   353	            groupedData[secondText].total += number;
   354	            groupedData[secondText].count += 1;
   355	          }
   356	        }
   357	      });
   358	
   359	      // Sort by total descending
   360	      const sortedEntries = Object.entries(groupedData).sort((a, b) => b[1].total - a[1].total);
   361	
   362	      // Add rows to summary table
   363	      sortedEntries.forEach(([secondText, data]) => {
   364	        const newRow = document.createElement('tr');
   365	        newRow.innerHTML = `
   366	          <td>${secondText}</td>
   367	          <td style="text-align: center; font-weight: bold;">${data.total}</td>
   368	          <td style="text-align: center;">${data.count}</td>
   369	        `;
   370	        summaryTable.appendChild(newRow);
   371	      });
   372	
   373	      // Add total row
   374	      if (sortedEntries.length > 0) {
   375	        const grandTotal = sortedEntries.reduce((sum, [, data]) => sum + data.total, 0);
   376	        const totalCount = sortedEntries.reduce((sum, [, data]) => sum + data.count, 0);
   377	
   378	        const totalRow = document.createElement('tr');
   379	        totalRow.style.backgroundColor = '#f8f9fa';
   380	        totalRow.style.fontWeight = 'bold';
   381	        totalRow.innerHTML = `
   382	          <td style="text-align: center;">TỔNG CỘNG</td>
   383	          <td style="text-align: center; color: #dc3545;">${grandTotal}</td>
   384	          <td style="text-align: center;">${totalCount}</td>
   385	        `;
   386	        summaryTable.appendChild(totalRow);
   387	      }
   388	    }
   389	
   390	    function addFilterFunctionality() {
   391	      const monthFilter = document.getElementById('monthFilter');
   392	
   393	      monthFilter.addEventListener('change', function() {
   394	        const selectedMonth = this.value;
   395	        filterTableByMonth(selectedMonth);
   396	
   397	        // Save filter to localStorage
   398	        localStorage.setItem('monthFilter', selectedMonth);
   399	      });
   400	
   401	      // Populate filter with available months
   402	      populateMonthFilter();
   403	
   404	      // Load saved filter
   405	      const savedFilter = localStorage.getItem('monthFilter');
   406	      if (savedFilter) {
   407	        monthFilter.value = savedFilter;
   408	        filterTableByMonth(savedFilter);
   409	      }
   410	    }
   411	
   412	    function populateMonthFilter() {
   413	      const monthFilter = document.getElementById('monthFilter');
   414	      const tableBody = document.querySelector('#dataTable tbody');
   415	      const rows = tableBody.querySelectorAll('tr');
   416	
   417	      // Get unique months from data
   418	      const availableMonths = new Set();
   419	
   420	      rows.forEach(row => {
   421	        const dateCell = row.querySelector('td:nth-child(4)'); // Date column
   422	        if (dateCell) {
   423	          const dateText = dateCell.textContent.trim(); // Format: DD/MM
   424	          const dateParts = dateText.split('/');
   425	          if (dateParts.length >= 2) {
   426	            const month = dateParts[1]; // MM part
   427	            availableMonths.add(month);
   428	          }
   429	        }
   430	      });
   431	
   432	      // Clear existing options (except "All")
   433	      const allOption = monthFilter.querySelector('option[value=""]');
   434	      monthFilter.innerHTML = '';
   435	      monthFilter.appendChild(allOption);
   436	
   437	      // Month names mapping
   438	      const monthNames = {
   439	        '01': 'Tháng 1', '02': 'Tháng 2', '03': 'Tháng 3', '04': 'Tháng 4',
   440	        '05': 'Tháng 5', '06': 'Tháng 6', '07': 'Tháng 7', '08': 'Tháng 8',
   441	        '09': 'Tháng 9', '10': 'Tháng 10', '11': 'Tháng 11', '12': 'Tháng 12'
   442	      };
   443	
   444	      // Sort months and add to dropdown
   445	      const sortedMonths = Array.from(availableMonths).sort();
   446	      sortedMonths.forEach(month => {
   447	        const option = document.createElement('option');
   448	        option.value = month;
   449	        option.textContent = monthNames[month] || month;
   450	        monthFilter.appendChild(option);
   451	      });
   452	    }
   453	
   454	    function filterTableByMonth(month) {
   455	      const tableBody = document.querySelector('#dataTable tbody');
   456	      const rows = tableBody.querySelectorAll('tr');
   457	
   458	      rows.forEach(row => {
   459	        const dateCell = row.querySelector('td:nth-child(4)'); // Date column
   460	        if (dateCell) {
   461	          const dateText = dateCell.textContent.trim(); // Format: DD/MM
   462	
   463	          if (month === '' || month === null) {
   464	            // Show all rows
   465	            row.style.display = '';
   466	          } else {
   467	            // Check if date matches selected month
   468	            const dateParts = dateText.split('/');
   469	            if (dateParts.length >= 2) {
   470	              const rowMonth = dateParts[1]; // MM part
   471	              if (rowMonth === month) {
   472	                row.style.display = '';
   473	              } else {
   474	                row.style.display = 'none';
   475	              }
   476	            } else {
   477	              row.style.display = 'none';
   478	            }
   479	          }
   480	        }
   481	      });
   482	
   483	      // Update summary table with filtered data
   484	      updateSummaryTable();
   485	    }
   486	
   487	    function addSortFunctionality() {
   488	      const sortSTTBtn = document.getElementById('sortSTT');
   489	      let sortOrder = 'desc'; // Current sort order: 'desc' = newest first, 'asc' = oldest first
   490	
   491	      sortSTTBtn.addEventListener('click', function() {
   492	        // Toggle sort order
   493	        sortOrder = sortOrder === 'desc' ? 'asc' : 'desc';
   494	
   495	        // Update button icon
   496	        sortSTTBtn.textContent = sortOrder === 'desc' ? '↓' : '↑';
   497	
   498	        // Sort table
