   211	        const sortedData = tableData.sort((a, b) => parseInt(b.stt || 0) - parseInt(a.stt || 0));
   212	
   213	        sortedData.forEach(rowData => {
   214	          const newRow = document.createElement('tr');
   215	          newRow.innerHTML = `
   216	            <td class="stt-cell">${rowData.stt || ''}</td>
   217	            <td class="editable-cell" data-field="text1">${rowData.text1}</td>
   218	            <td class="editable-cell" data-field="number">${rowData.number}</td>
   219	            <td class="editable-cell" data-field="date">${rowData.date}</td>
   220	            <td class="editable-cell" data-field="text2">${rowData.text2}</td>
   221	          `;
   222	          tableBody.appendChild(newRow);
   223	        });
   224	
   225	        // Apply saved sort order
   226	        const savedSortOrder = localStorage.getItem('sortOrder') || 'desc';
   227	        sortTableBySTT(savedSortOrder);
   228	
   229	        // Update sort button icon
   230	        const sortSTTBtn = document.getElementById('sortSTT');
   231	        if (sortSTTBtn) {
   232	          sortSTTBtn.textContent = savedSortOrder === 'desc' ? '↓' : '↑';
   233	        }
   234	
   235	        // Update month filter options
   236	        populateMonthFilter();
   237	
   238	        // Update summary table after loading data
   239	        updateSummaryTable();
   240	      }
   241	    }
