   265	    function addClipboardFunctionality() {
   266	      const pasteBtn = document.getElementById('pasteBtn');
   267	      const pasteBtnText2 = document.getElementById('pasteBtnText2');
   268	      const largeTextBox = document.getElementById('largeTextBox');
   269	      const textbox2 = document.getElementById('textbox2');
   270	
   271	      // Paste button for Notes/Comments
   272	      pasteBtn.addEventListener('click', async function() {
   273	        try {
   274	          // Check if clipboard API is supported
   275	          if (!navigator.clipboard) {
   276	            alert('Clipboard API không được hỗ trợ trên trình duyệt này');
   277	            return;
   278	          }
   279	
   280	          // Read text from clipboard
   281	          const clipboardText = await navigator.clipboard.readText();
   282	
   283	          if (clipboardText) {
   284	            // Paste into largeTextBox
   285	            largeTextBox.value = clipboardText;
   286	
   287	            // Trigger parse function
   288	            parseLargeTextData();
   289	
   290	            // Show success message
   291	            console.log('Đã paste từ clipboard vào Notes:', clipboardText);
   292	          } else {
   293	            alert('Clipboard trống');
   294	          }
   295	        } catch (error) {
   296	          console.error('Lỗi khi đọc clipboard:', error);
   297	          alert('Không thể đọc clipboard. Vui lòng cho phép quyền truy cập clipboard.');
   298	        }
   299	      });
   300	
   301	      // Paste button for Second Text
   302	      pasteBtnText2.addEventListener('click', async function() {
   303	        try {
   304	          // Check if clipboard API is supported
   305	          if (!navigator.clipboard) {
   306	            alert('Clipboard API không được hỗ trợ trên trình duyệt này');
   307	            return;
   308	          }
   309	
   310	          // Read text from clipboard
   311	          const clipboardText = await navigator.clipboard.readText();
   312	
   313	          if (clipboardText) {
   314	            // Paste into textbox2
   315	            textbox2.value = clipboardText;
   316	
   317	            // Show success message
   318	            console.log('Đã paste từ clipboard vào Second Text:', clipboardText);
   319	          } else {
   320	            alert('Clipboard trống');
   321	          }
   322	        } catch (error) {
   323	          console.error('Lỗi khi đọc clipboard:', error);
   324	          alert('Không thể đọc clipboard. Vui lòng cho phép quyền truy cập clipboard.');
   325	        }
   326	      });
   327	    }
