   249	    function addSelectAllFunctionality() {
   250	      // Get all input and textarea elements
   251	      const inputs = document.querySelectorAll('input[type="text"], input[type="number"], textarea');
   252	
   253	      inputs.forEach(input => {
   254	        // Skip date picker for select all (it has special functionality)
   255	        if (input.id === 'datePicker' || input.id === 'datePickerHidden') return;
   256	
   257	        // Add click event listener to select all text
   258	        input.addEventListener('click', function() {
   259	          this.select();
   260	        });
   261	
   262	        // Add focus event listener to select all text (for keyboard navigation)
   263	        input.addEventListener('focus', function() {
   264	          this.select();
   265	        });
   266	      });
   267	    }
