   396	    function addFilterFunctionality() {
   397	      const monthFilter = document.getElementById('monthFilter');
   398	
   399	      monthFilter.addEventListener('change', function() {
   400	        const selectedMonth = this.value;
   401	        filterTableByMonth(selectedMonth);
   402	
   403	        // Save filter to localStorage
   404	        localStorage.setItem('monthFilter', selectedMonth);
   405	      });
   406	
   407	      // Populate filter with available months
   408	      populateMonthFilter();
   409	
   410	      // Load saved filter
   411	      const savedFilter = localStorage.getItem('monthFilter');
   412	      if (savedFilter) {
   413	        monthFilter.value = savedFilter;
   414	        filterTableByMonth(savedFilter);
   415	      }
   416	    }
