   418	    function populateMonthFilter() {
   419	      const monthFilter = document.getElementById('monthFilter');
   420	      const tableBody = document.querySelector('#dataTable tbody');
   421	      const rows = tableBody.querySelectorAll('tr');
   422	
   423	      // Get unique months from data
   424	      const availableMonths = new Set();
   425	
   426	      rows.forEach(row => {
   427	        const dateCell = row.querySelector('td:nth-child(4)'); // Date column
   428	        if (dateCell) {
   429	          const dateText = dateCell.textContent.trim(); // Format: D/M or DD/MM
   430	          const dateParts = dateText.split('/');
   431	          if (dateParts.length >= 2) {
   432	            const month = dateParts[1].padStart(2, '0'); // Convert M to MM format for consistency
   433	            availableMonths.add(month);
   434	          }
   435	        }
   436	      });
   437	
   438	      // Clear existing options (except "All")
   439	      const allOption = monthFilter.querySelector('option[value=""]');
   440	      monthFilter.innerHTML = '';
   441	      monthFilter.appendChild(allOption);
   442	
   443	      // Month names mapping
   444	      const monthNames = {
   445	        '01': 'Tháng 1', '02': 'Tháng 2', '03': 'Tháng 3', '04': 'Tháng 4',
   446	        '05': 'Tháng 5', '06': 'Tháng 6', '07': 'Tháng 7', '08': 'Tháng 8',
   447	        '09': 'Tháng 9', '10': 'Tháng 10', '11': 'Tháng 11', '12': 'Tháng 12'
   448	      };
   449	
   450	      // Sort months and add to dropdown
   451	      const sortedMonths = Array.from(availableMonths).sort();
   452	      sortedMonths.forEach(month => {
   453	        const option = document.createElement('option');
   454	        option.value = month;
   455	        option.textContent = monthNames[month] || month;
   456	        monthFilter.appendChild(option);
   457	      });
   458	    }
   459	
   460	    function filterTableByMonth(month) {
   461	      const tableBody = document.querySelector('#dataTable tbody');
   462	      const rows = tableBody.querySelectorAll('tr');
   463	
   464	      rows.forEach(row => {
   465	        const dateCell = row.querySelector('td:nth-child(4)'); // Date column
   466	        if (dateCell) {
   467	          const dateText = dateCell.textContent.trim(); // Format: D/M or DD/MM
   468	
   469	          if (month === '' || month === null) {
   470	            // Show all rows
   471	            row.style.display = '';
   472	          } else {
   473	            // Check if date matches selected month
   474	            const dateParts = dateText.split('/');
   475	            if (dateParts.length >= 2) {
   476	              const rowMonth = dateParts[1].padStart(2, '0'); // Convert M to MM for comparison
   477	              if (rowMonth === month) {
   478	                row.style.display = '';
   479	              } else {
   480	                row.style.display = 'none';
   481	              }
   482	            } else {
   483	              row.style.display = 'none';
   484	            }
   485	          }
   486	        }
   487	      });
   488	
   489	      // Update summary table with filtered data
   490	      updateSummaryTable();
   491	    }
   492	
   493	    function addSortFunctionality() {
   494	      const sortSTTBtn = document.getElementById('sortSTT');
   495	      let sortOrder = 'desc'; // Current sort order: 'desc' = newest first, 'asc' = oldest first
   496	
   497	      sortSTTBtn.addEventListener('click', function() {
   498	        // Toggle sort order
   499	        sortOrder = sortOrder === 'desc' ? 'asc' : 'desc';
   500	
   501	        // Update button icon
   502	        sortSTTBtn.textContent = sortOrder === 'desc' ? '↓' : '↑';
   503	
   504	        // Sort table
   505	        sortTableBySTT(sortOrder);
   506	      });
   507	
   508	      // Initialize button icon
   509	      sortSTTBtn.textContent = '↓'; // Default: newest first (descending)
   510	    }
   511	
   512	    function sortTableBySTT(order) {
   513	      const tableBody = document.querySelector('#dataTable tbody');
   514	      const rows = Array.from(tableBody.querySelectorAll('tr'));
   515	
   516	      // Sort rows by STT
   517	      rows.sort((a, b) => {
   518	        const sttA = parseInt(a.querySelector('td:first-child').textContent) || 0;
   519	        const sttB = parseInt(b.querySelector('td:first-child').textContent) || 0;
   520	
   521	        if (order === 'asc') {
   522	          return sttA - sttB; // Ascending: 1, 2, 3...
   523	        } else {
   524	          return sttB - sttA; // Descending: 3, 2, 1...
   525	        }
   526	      });
   527	
   528	      // Clear table and re-add sorted rows
   529	      tableBody.innerHTML = '';
   530	      rows.forEach(row => {
   531	        tableBody.appendChild(row);
   532	      });
   533	
   534	      // Save current sort order to localStorage
   535	      localStorage.setItem('sortOrder', order);
   536	    }
