   460	    function filterTableByMonth(month) {
   461	      const tableBody = document.querySelector('#dataTable tbody');
   462	      const rows = tableBody.querySelectorAll('tr');
   463	
   464	      rows.forEach(row => {
   465	        const dateCell = row.querySelector('td:nth-child(4)'); // Date column
   466	        if (dateCell) {
   467	          const dateText = dateCell.textContent.trim(); // Format: D/M or DD/MM
   468	
   469	          if (month === '' || month === null) {
   470	            // Show all rows
   471	            row.style.display = '';
   472	          } else {
   473	            // Check if date matches selected month
   474	            const dateParts = dateText.split('/');
   475	            if (dateParts.length >= 2) {
   476	              const rowMonth = dateParts[1].padStart(2, '0'); // Convert M to MM for comparison
   477	              if (rowMonth === month) {
   478	                row.style.display = '';
   479	              } else {
   480	                row.style.display = 'none';
   481	              }
   482	            } else {
   483	              row.style.display = 'none';
   484	            }
   485	          }
   486	        }
   487	      });
   488	
   489	      // Update summary table with filtered data
   490	      updateSummaryTable();
   491	    }
