   136	    function addToTable() {
   137	      const text1 = document.getElementById('textbox1').value;
   138	      const text2 = document.getElementById('textbox2').value;
   139	      const date = document.getElementById('datePickerHidden').value;
   140	      const number = document.getElementById('numberSelect').value;
   141	      
   142	      if (text1 && text2 && date) {
   143	        // Format date from YYYY-MM-DD to D/M (remove leading zeros)
   144	        const [year, month, day] = date.split('-');
   145	        const formattedDate = `${parseInt(day)}/${parseInt(month)}`;
   146	
   147	        // Format First Text according to new format: "<textbox1>. Tắm <number>b <textbox2>"
   148	        // Check if textbox1 already ends with "." to avoid duplication
   149	        const separator = text1.trim().endsWith('.') ? ' ' : '. ';
   150	        const formattedText1 = `${text1}${separator}Tắm ${number}b ${text2}`;
   151	
   152	        // Add to table
   153	        const tableBody = document.querySelector('#dataTable tbody');
   154	        const newRow = document.createElement('tr');
   155	
   156	        // Calculate STT (current number of rows + 1) - newest gets highest number
   157	        const currentRowCount = tableBody.querySelectorAll('tr').length + 1;
   158	
   159	        newRow.innerHTML = `
   160	          <td class="stt-cell">${currentRowCount}</td>
   161	          <td class="editable-cell" data-field="text1">${formattedText1}</td>
   162	          <td class="editable-cell" data-field="number">${number}</td>
   163	          <td class="editable-cell" data-field="date">${formattedDate}</td>
   164	          <td class="editable-cell" data-field="text2">${text2}</td>
   165	        `;
   166	
   167	        // Insert new row at the top (newest first)
   168	        tableBody.insertBefore(newRow, tableBody.firstChild);
   169	        
   170	        // Save to localStorage
   171	        saveTableData();
   172	
   173	        // Update summary table
   174	        updateSummaryTable();
   175	
   176	        // Update month filter options
   177	        populateMonthFilter();
   178	        
   179	        // Save second text value
   180	        localStorage.setItem('secondText', text2);
   181	        
   182	        // Clear only the first text field, keep the second
   183	        document.getElementById('textbox1').value = '';
   184	        document.getElementById('numberSelect').value = '0';
   185	        // Keep date and second text
   186	      } else {
   187	        alert('Please fill in all required fields');
   188	      }
   189	    }
