   271	    function addClipboardFunctionality() {
   272	      const pasteBtn = document.getElementById('pasteBtn');
   273	      const pasteBtnText2 = document.getElementById('pasteBtnText2');
   274	      const largeTextBox = document.getElementById('largeTextBox');
   275	      const textbox2 = document.getElementById('textbox2');
   276	
   277	      // Paste button for Notes/Comments
   278	      pasteBtn.addEventListener('click', async function() {
   279	        try {
   280	          // Check if clipboard API is supported
   281	          if (!navigator.clipboard) {
   282	            alert('Clipboard API không được hỗ trợ trên trình duyệt này');
   283	            return;
   284	          }
   285	
   286	          // Read text from clipboard
   287	          const clipboardText = await navigator.clipboard.readText();
   288	
   289	          if (clipboardText) {
   290	            // Paste into largeTextBox
   291	            largeTextBox.value = clipboardText;
   292	
   293	            // Trigger parse function
   294	            parseLargeTextData();
   295	
   296	            // Show success message
   297	            console.log('Đã paste từ clipboard vào Notes:', clipboardText);
   298	          } else {
   299	            alert('Clipboard trống');
   300	          }
   301	        } catch (error) {
   302	          console.error('Lỗi khi đọc clipboard:', error);
   303	          alert('Không thể đọc clipboard. Vui lòng cho phép quyền truy cập clipboard.');
   304	        }
   305	      });
   306	
   307	      // Paste button for Second Text
   308	      pasteBtnText2.addEventListener('click', async function() {
   309	        try {
   310	          // Check if clipboard API is supported
   311	          if (!navigator.clipboard) {
   312	            alert('Clipboard API không được hỗ trợ trên trình duyệt này');
   313	            return;
   314	          }
   315	
   316	          // Read text from clipboard
   317	          const clipboardText = await navigator.clipboard.readText();
   318	
   319	          if (clipboardText) {
   320	            // Paste into textbox2
   321	            textbox2.value = clipboardText;
   322	
   323	            // Show success message
   324	            console.log('Đã paste từ clipboard vào Second Text:', clipboardText);
   325	          } else {
   326	            alert('Clipboard trống');
   327	          }
   328	        } catch (error) {
   329	          console.error('Lỗi khi đọc clipboard:', error);
   330	          alert('Không thể đọc clipboard. Vui lòng cho phép quyền truy cập clipboard.');
   331	        }
   332	      });
   333	    }
