   538	    function addTableEditFunctionality() {
   539	      const table = document.getElementById('dataTable');
   540	
   541	      // Add click event listener to table for event delegation
   542	      table.addEventListener('click', function(e) {
   543	        const cell = e.target;
   544	
   545	        // Only allow editing of editable cells
   546	        if (!cell.classList.contains('editable-cell') || cell.querySelector('input')) {
   547	          return;
   548	        }
   549	
   550	        // Get current value
   551	        const currentValue = cell.textContent;
   552	        const field = cell.getAttribute('data-field');
   553	
   554	        // Create input element
   555	        let input;
   556	        if (field === 'number') {
   557	          input = document.createElement('input');
   558	          input.type = 'number';
   559	          input.min = '0';
   560	          input.max = '100';
   561	        } else if (field === 'date') {
   562	          input = document.createElement('input');
   563	          input.type = 'text';
   564	          input.placeholder = 'DD/MM';
   565	          input.maxLength = '5';
   566	        } else {
   567	          input = document.createElement('input');
   568	          input.type = 'text';
   569	        }
   570	
   571	        input.value = currentValue;
   572	        input.style.width = '100%';
   573	        input.style.border = 'none';
   574	        input.style.padding = '4px';
   575	        input.style.fontSize = 'inherit';
   576	        input.style.fontFamily = 'inherit';
   577	
   578	        // Replace cell content with input
   579	        cell.innerHTML = '';
   580	        cell.appendChild(input);
   581	        input.focus();
   582	        input.select();
   583	
   584	        // Save on blur or Enter
   585	        function saveEdit() {
   586	          let newValue = input.value.trim();
   587	
   588	          // Validate based on field type
   589	          if (field === 'number') {
   590	            const num = parseInt(newValue);
   591	            if (isNaN(num) || num < 0 || num > 100) {
   592	              newValue = currentValue; // Revert to original
   593	            }
   594	          } else if (field === 'date') {
   595	            // Auto-format date
   596	            if (newValue.length > 0 && !newValue.includes('/')) {
   597	              if (newValue.length >= 2) {
   598	                newValue = newValue.substring(0, 2) + '/' + newValue.substring(2, 4);
   599	              }
   600	            }
   601	            // Validate DD/MM format
   602	            if (newValue && !/^\d{2}\/\d{2}$/.test(newValue)) {
   603	              newValue = currentValue; // Revert to original
   604	            }
   605	          }
   606	
   607	          cell.textContent = newValue;
   608	
   609	          // Save to localStorage
   610	          saveTableData();
   611	
   612	          // Update summary table
   613	          updateSummaryTable();
   614	        }
   615	
   616	        input.addEventListener('blur', saveEdit);
   617	        input.addEventListener('keydown', function(e) {
   618	          if (e.key === 'Enter') {
   619	            input.blur();
   620	          } else if (e.key === 'Escape') {
   621	            cell.textContent = currentValue; // Revert
   622	          }
   623	        });
   624	      });
   625	    }
   626	
   627	    function parseLargeTextData() {
   628	      const largeTextValue = document.getElementById('largeTextBox').value;
   629	
   630	      if (largeTextValue.trim() === '') {
   631	        document.getElementById('textbox1').value = '';
   632	        document.getElementById('numberSelect').value = '0';
   633	        return;
   634	      }
   635	
   636	      // Parse logic: textbox1 gets everything from start to "tắm"
   637	      let textbox1Value = '';
   638	      let numberValue = '0';
   639	
   640	      // Extract everything from start to "tắm" for textbox1
   641	      // Convert to lowercase for searching "tắm"
   642	      const lowerCaseText = largeTextValue.toLowerCase();
   643	      const tamIndex = lowerCaseText.indexOf('tắm');
   644	      if (tamIndex !== -1) {
   645	        textbox1Value = largeTextValue.substring(0, tamIndex).trim();
   646	
   647	        // Extract number using pattern "tắm .*(\d+)b" for number select
   648	        const afterTam = largeTextValue.substring(tamIndex);
   649	        // Look for pattern "tắm.*(\d+)b" (case insensitive, flexible spacing)
   650	        const numberMatch = afterTam.match(/tắm\s*.*?(\d+)b/i);
   651	        if (numberMatch) {
   652	          const extractedNumber = parseInt(numberMatch[1]);
   653	          // Make sure the number is within the valid range (0-100)
   654	          if (extractedNumber >= 0 && extractedNumber <= 100) {
   655	            numberValue = extractedNumber.toString();
   656	          }
   657	        }
   658	
   659	        // Debug: log the values for testing
   660	        console.log('afterTam:', afterTam);
   661	        console.log('numberMatch:', numberMatch);
   662	        console.log('extractedNumber:', numberValue);
   663	      } else {
   664	        // If "tắm" not found, use the whole text
   665	        textbox1Value = largeTextValue.trim();
   666	      }
   667	
   668	      // Update textbox1 and numberSelect
   669	      document.getElementById('textbox1').value = textbox1Value;
   670	      document.getElementById('numberSelect').value = numberValue;
   671	    }
   672	
   673	    function copyTableToClipboard() {
   674	      const table = document.getElementById('dataTable');
   675	      const rows = table.querySelectorAll('tbody tr'); // Only get data rows, skip header
   676	
   677	      // Create array to store table data
   678	      const tableData = [];
   679	
   680	      // Process each data row (skip header)
   681	      rows.forEach((row, rowIndex) => {
   682	        const cells = row.querySelectorAll('td');
   683	
   684	        // Check if date column has D/M or DD/MM format before processing row
   685	        const dateCell = cells[3]; // Date column (index 3)
   686	        if (dateCell) {
   687	          const dateText = dateCell.textContent.trim();
   688	          // Only process rows with D/M or DD/MM format (contains '/' and valid length)
   689	          if (!dateText.includes('/') || dateText.length < 3 || dateText.length > 5) {
   690	            return; // Skip this row if date is not D/M or DD/MM format
   691	          }
   692	          // Additional validation: check if it's a valid date format (D/M, DD/M, D/MM, DD/MM)
   693	          const dateParts = dateText.split('/');
   694	          if (dateParts.length !== 2 || !dateParts[0] || !dateParts[1]) {
   695	            return; // Skip if not valid D/M format
   696	          }
   697	        } else {
   698	          return; // Skip if no date cell
   699	        }
   700	
   701	        const rowData = [];
   702	
   703	        cells.forEach((cell, cellIndex) => {
   704	          // Skip STT column (first column, index 0)
   705	          if (cellIndex === 0) return;
   706	
   707	          // Get text content and clean it up
   708	          let cellText = cell.textContent.trim();
   709	
   710	          // Special handling for Date column (index 3 in original table, index 2 after skipping STT)
   711	          if (cellIndex === 3) {
   712	            // Keep DD/MM format as is (no special formatting)
   713	            // cellText remains unchanged for DD/MM format
   714	          }
   715	
   716	          // Handle cells that might contain commas or quotes for CSV format
   717	          if (cellText.includes(',') || cellText.includes('"') || cellText.includes('\n')) {
   718	            cellText = `"${cellText.replace(/"/g, '""')}"`;
   719	          }
   720	
   721	          rowData.push(cellText);
   722	        });
   723	
   724	        tableData.push(rowData.join('\t')); // Use tab separator for better Google Sheets compatibility
   725	      });
   726	
   727	      // Join all rows with newlines
   728	      const tableText = tableData.join('\n');
   729	
   730	      // Copy to clipboard
   731	      navigator.clipboard.writeText(tableText).then(() => {
   732	        // Show success message
   733	        const copyBtn = document.getElementById('copyTableBtn');
   734	        const originalText = copyBtn.textContent;
   735	        copyBtn.textContent = '✅ Copied!';
   736	        copyBtn.style.backgroundColor = '#28a745';
   737	
   738	        // Reset button after 2 seconds
   739	        setTimeout(() => {
   740	          copyBtn.textContent = originalText;
   741	          copyBtn.style.backgroundColor = '#28a745';
   742	        }, 2000);
   743	
   744	        console.log('Table data copied to clipboard');
   745	      }).catch(err => {
   746	        console.error('Failed to copy table data: ', err);
   747	        alert('Không thể copy dữ liệu. Vui lòng thử lại.');
   748	      });
   749	    }
