   191	    function saveTableData() {
   192	      const tableRows = document.querySelectorAll('#dataTable tbody tr');
   193	      const tableData = [];
   194	      
   195	      tableRows.forEach(row => {
   196	        const cells = row.querySelectorAll('td');
