   209	    function loadTableData() {
   210	      const tableBody = document.querySelector('#dataTable tbody');
   211	      const savedData = localStorage.getItem('tableData');
   212	
   213	      if (savedData) {
   214	        const tableData = JSON.parse(savedData);
   215	
   216	        // Sort data by STT descending (newest first) before displaying
   217	        const sortedData = tableData.sort((a, b) => parseInt(b.stt || 0) - parseInt(a.stt || 0));
   218	
   219	        sortedData.forEach(rowData => {
   220	          const newRow = document.createElement('tr');
   221	          newRow.innerHTML = `
   222	            <td class="stt-cell">${rowData.stt || ''}</td>
   223	            <td class="editable-cell" data-field="text1">${rowData.text1}</td>
   224	            <td class="editable-cell" data-field="number">${rowData.number}</td>
