   224	            <td class="editable-cell" data-field="number">${rowData.number}</td>
   225	            <td class="editable-cell" data-field="date">${rowData.date}</td>
   226	            <td class="editable-cell" data-field="text2">${rowData.text2}</td>
   227	          `;
   228	          tableBody.appendChild(newRow);
   229	        });
   230	
   231	        // Apply saved sort order
   232	        const savedSortOrder = localStorage.getItem('sortOrder') || 'desc';
   233	        sortTableBySTT(savedSortOrder);
   234	
   235	        // Update sort button icon
   236	        const sortSTTBtn = document.getElementById('sortSTT');
   237	        if (sortSTTBtn) {
   238	          sortSTTBtn.textContent = savedSortOrder === 'desc' ? '↓' : '↑';
   239	        }
   240	
   241	        // Update month filter options
   242	        populateMonthFilter();
   243	
   244	        // Update summary table after loading data
   245	        updateSummaryTable();
   246	      }
   247	    }
