   271	    function addClipboardFunctionality() {
   272	      const pasteBtn = document.getElementById('pasteBtn');
   273	      const pasteBtnText2 = document.getElementById('pasteBtnText2');
   274	      const largeTextBox = document.getElementById('largeTextBox');
   275	      const textbox2 = document.getElementById('textbox2');
   276	
   277	      // Paste button for Notes/Comments
   278	      pasteBtn.addEventListener('click', async function() {
   279	        try {
   280	          // Check if clipboard API is supported
   281	          if (!navigator.clipboard) {
   282	            alert('Clipboard API không được hỗ trợ trên trình duyệt này');
   283	            return;
   284	          }
   285	
   286	          // Read text from clipboard
   287	          const clipboardText = await navigator.clipboard.readText();
   288	
   289	          if (clipboardText) {
   290	            // Paste into largeTextBox
   291	            largeTextBox.value = clipboardText;
   292	
   293	            // Trigger parse function
   294	            parseLargeTextData();
   295	
   296	            // Show success message
   297	            console.log('Đã paste từ clipboard vào Notes:', clipboardText);
   298	          } else {
   299	            alert('Clipboard trống');
   300	          }
   301	        } catch (error) {
   302	          console.error('Lỗi khi đọc clipboard:', error);
   303	          alert('Không thể đọc clipboard. Vui lòng cho phép quyền truy cập clipboard.');
   304	        }
   305	      });
   306	
   307	      // Paste button for Second Text
   308	      pasteBtnText2.addEventListener('click', async function() {
   309	        try {
   310	          // Check if clipboard API is supported
   311	          if (!navigator.clipboard) {
   312	            alert('Clipboard API không được hỗ trợ trên trình duyệt này');
   313	            return;
   314	          }
   315	
   316	          // Read text from clipboard
   317	          const clipboardText = await navigator.clipboard.readText();
   318	
   319	          if (clipboardText) {
   320	            // Paste into textbox2
   321	            textbox2.value = clipboardText;
   322	
   323	            // Show success message
   324	            console.log('Đã paste từ clipboard vào Second Text:', clipboardText);
   325	          } else {
   326	            alert('Clipboard trống');
   327	          }
   328	        } catch (error) {
   329	          console.error('Lỗi khi đọc clipboard:', error);
   330	          alert('Không thể đọc clipboard. Vui lòng cho phép quyền truy cập clipboard.');
   331	        }
   332	      });
   333	    }
   334	
   335	    function updateSummaryTable() {
   336	      const dataTable = document.querySelector('#dataTable tbody');
   337	      const summaryTable = document.querySelector('#summaryTable tbody');
   338	      const rows = dataTable.querySelectorAll('tr:not([style*="display: none"])');
   339	
   340	      // Clear summary table
   341	      summaryTable.innerHTML = '';
   342	
   343	      // Group data by second text
   344	      const groupedData = {};
   345	
   346	      rows.forEach(row => {
   347	        const cells = row.querySelectorAll('td');
   348	        if (cells.length >= 5) {
   349	          const secondText = cells[4].textContent.trim(); // Second Text column (at index 4)
   350	          const number = parseInt(cells[2].textContent) || 0; // Number column (now at index 2)
   351	
   352	          if (secondText) {
   353	            if (!groupedData[secondText]) {
   354	              groupedData[secondText] = {
   355	                total: 0,
   356	                count: 0
   357	              };
   358	            }
   359	            groupedData[secondText].total += number;
   360	            groupedData[secondText].count += 1;
   361	          }
   362	        }
   363	      });
   364	
   365	      // Sort by total descending
   366	      const sortedEntries = Object.entries(groupedData).sort((a, b) => b[1].total - a[1].total);
   367	
   368	      // Add rows to summary table
   369	      sortedEntries.forEach(([secondText, data]) => {
   370	        const newRow = document.createElement('tr');
   371	        newRow.innerHTML = `
   372	          <td>${secondText}</td>
   373	          <td style="text-align: center; font-weight: bold;">${data.total}</td>
   374	          <td style="text-align: center;">${data.count}</td>
   375	        `;
   376	        summaryTable.appendChild(newRow);
   377	      });
   378	
   379	      // Add total row
   380	      if (sortedEntries.length > 0) {
   381	        const grandTotal = sortedEntries.reduce((sum, [, data]) => sum + data.total, 0);
   382	        const totalCount = sortedEntries.reduce((sum, [, data]) => sum + data.count, 0);
   383	
   384	        const totalRow = document.createElement('tr');
   385	        totalRow.style.backgroundColor = '#f8f9fa';
   386	        totalRow.style.fontWeight = 'bold';
   387	        totalRow.innerHTML = `
   388	          <td style="text-align: center;">TỔNG CỘNG</td>
   389	          <td style="text-align: center; color: #dc3545;">${grandTotal}</td>
   390	          <td style="text-align: center;">${totalCount}</td>
   391	        `;
   392	        summaryTable.appendChild(totalRow);
   393	      }
   394	    }
