   396	    function addFilterFunctionality() {
   397	      const monthFilter = document.getElementById('monthFilter');
   398	
   399	      monthFilter.addEventListener('change', function() {
   400	        const selectedMonth = this.value;
   401	        filterTableByMonth(selectedMonth);
   402	
   403	        // Save filter to localStorage
   404	        localStorage.setItem('monthFilter', selectedMonth);
   405	      });
   406	
   407	      // Populate filter with available months
   408	      populateMonthFilter();
   409	
   410	      // Load saved filter
   411	      const savedFilter = localStorage.getItem('monthFilter');
   412	      if (savedFilter) {
   413	        monthFilter.value = savedFilter;
   414	        filterTableByMonth(savedFilter);
   415	      }
   416	    }
   417	
   418	    function populateMonthFilter() {
   419	      const monthFilter = document.getElementById('monthFilter');
   420	      const tableBody = document.querySelector('#dataTable tbody');
   421	      const rows = tableBody.querySelectorAll('tr');
   422	
   423	      // Get unique months from data
   424	      const availableMonths = new Set();
   425	
   426	      rows.forEach(row => {
   427	        const dateCell = row.querySelector('td:nth-child(4)'); // Date column
   428	        if (dateCell) {
   429	          const dateText = dateCell.textContent.trim(); // Format: D/M or DD/MM
   430	          const dateParts = dateText.split('/');
   431	          if (dateParts.length >= 2) {
   432	            const month = dateParts[1].padStart(2, '0'); // Convert M to MM format for consistency
   433	            availableMonths.add(month);
   434	          }
   435	        }
   436	      });
   437	
   438	      // Clear existing options (except "All")
   439	      const allOption = monthFilter.querySelector('option[value=""]');
   440	      monthFilter.innerHTML = '';
   441	      monthFilter.appendChild(allOption);
   442	
   443	      // Month names mapping
   444	      const monthNames = {
   445	        '01': 'Tháng 1', '02': 'Tháng 2', '03': 'Tháng 3', '04': 'Tháng 4',
   446	        '05': 'Tháng 5', '06': 'Tháng 6', '07': 'Tháng 7', '08': 'Tháng 8',
   447	        '09': 'Tháng 9', '10': 'Tháng 10', '11': 'Tháng 11', '12': 'Tháng 12'
   448	      };
   449	
   450	      // Sort months and add to dropdown
   451	      const sortedMonths = Array.from(availableMonths).sort();
   452	      sortedMonths.forEach(month => {
   453	        const option = document.createElement('option');
   454	        option.value = month;
   455	        option.textContent = monthNames[month] || month;
   456	        monthFilter.appendChild(option);
   457	      });
   458	    }
   459	
   460	    function filterTableByMonth(month) {
   461	      const tableBody = document.querySelector('#dataTable tbody');
   462	      const rows = tableBody.querySelectorAll('tr');
   463	
   464	      rows.forEach(row => {
   465	        const dateCell = row.querySelector('td:nth-child(4)'); // Date column
   466	        if (dateCell) {
   467	          const dateText = dateCell.textContent.trim(); // Format: D/M or DD/MM
   468	
   469	          if (month === '' || month === null) {
   470	            // Show all rows
   471	            row.style.display = '';
   472	          } else {
   473	            // Check if date matches selected month
   474	            const dateParts = dateText.split('/');
   475	            if (dateParts.length >= 2) {
   476	              const rowMonth = dateParts[1].padStart(2, '0'); // Convert M to MM for comparison
   477	              if (rowMonth === month) {
   478	                row.style.display = '';
   479	              } else {
   480	                row.style.display = 'none';
   481	              }
   482	            } else {
   483	              row.style.display = 'none';
   484	            }
   485	          }
   486	        }
   487	      });
   488	
   489	      // Update summary table with filtered data
   490	      updateSummaryTable();
   491	    }
   492	
   493	    function addSortFunctionality() {
   494	      const sortSTTBtn = document.getElementById('sortSTT');
   495	      let sortOrder = 'desc'; // Current sort order: 'desc' = newest first, 'asc' = oldest first
   496	
   497	      sortSTTBtn.addEventListener('click', function() {
   498	        // Toggle sort order
   499	        sortOrder = sortOrder === 'desc' ? 'asc' : 'desc';
   500	
   501	        // Update button icon
   502	        sortSTTBtn.textContent = sortOrder === 'desc' ? '↓' : '↑';
   503	
   504	        // Sort table
   505	        sortTableBySTT(sortOrder);
   506	      });
   507	
   508	      // Initialize button icon
   509	      sortSTTBtn.textContent = '↓'; // Default: newest first (descending)
   510	    }
   511	
   512	    function sortTableBySTT(order) {
   513	      const tableBody = document.querySelector('#dataTable tbody');
   514	      const rows = Array.from(tableBody.querySelectorAll('tr'));
   515	
   516	      // Sort rows by STT
   517	      rows.sort((a, b) => {
   518	        const sttA = parseInt(a.querySelector('td:first-child').textContent) || 0;
   519	        const sttB = parseInt(b.querySelector('td:first-child').textContent) || 0;
   520	
   521	        if (order === 'asc') {
   522	          return sttA - sttB; // Ascending: 1, 2, 3...
   523	        } else {
   524	          return sttB - sttA; // Descending: 3, 2, 1...
   525	        }
   526	      });
   527	
   528	      // Clear table and re-add sorted rows
   529	      tableBody.innerHTML = '';
   530	      rows.forEach(row => {
   531	        tableBody.appendChild(row);
   532	      });
   533	
   534	      // Save current sort order to localStorage
   535	      localStorage.setItem('sortOrder', order);
   536	    }
   537	
   538	    function addTableEditFunctionality() {
   539	      const table = document.getElementById('dataTable');
   540	
   541	      // Add click event listener to table for event delegation
   542	      table.addEventListener('click', function(e) {
   543	        const cell = e.target;
   544	
   545	        // Only allow editing of editable cells
   546	        if (!cell.classList.contains('editable-cell') || cell.querySelector('input')) {
   547	          return;
   548	        }
   549	
   550	        // Get current value
   551	        const currentValue = cell.textContent;
   552	        const field = cell.getAttribute('data-field');
   553	
   554	        // Create input element
   555	        let input;
   556	        if (field === 'number') {
   557	          input = document.createElement('input');
   558	          input.type = 'number';
   559	          input.min = '0';
   560	          input.max = '100';
   561	        } else if (field === 'date') {
   562	          input = document.createElement('input');
   563	          input.type = 'text';
   564	          input.placeholder = 'D/M';
   565	          input.maxLength = '5';
   566	        } else {
   567	          input = document.createElement('input');
   568	          input.type = 'text';
   569	        }
   570	
   571	        input.value = currentValue;
   572	        input.style.width = '100%';
   573	        input.style.border = 'none';
   574	        input.style.padding = '4px';
   575	        input.style.fontSize = 'inherit';
   576	        input.style.fontFamily = 'inherit';
   577	
   578	        // Replace cell content with input
   579	        cell.innerHTML = '';
   580	        cell.appendChild(input);
   581	        input.focus();
   582	        input.select();
   583	
   584	        // Save on blur or Enter
   585	        function saveEdit() {
   586	          let newValue = input.value.trim();
   587	
   588	          // Validate based on field type
   589	          if (field === 'number') {
   590	            const num = parseInt(newValue);
   591	            if (isNaN(num) || num < 0 || num > 100) {
   592	              newValue = currentValue; // Revert to original
   593	            }
   594	          } else if (field === 'date') {
   595	            // Auto-format date
   596	            if (newValue.length > 0 && !newValue.includes('/')) {
   597	              if (newValue.length >= 2) {
   598	                newValue = newValue.substring(0, 2) + '/' + newValue.substring(2, 4);
   599	              }
   600	            }
   601	            // Validate D/M or DD/MM format and convert to D/M (remove leading zeros)
   602	            if (newValue && /^\d{1,2}\/\d{1,2}$/.test(newValue)) {
   603	              const [day, month] = newValue.split('/');
   604	              newValue = `${parseInt(day)}/${parseInt(month)}`;
   605	            } else if (newValue) {
   606	              newValue = currentValue; // Revert to original if invalid format
   607	            }
   608	          }
   609	
   610	          cell.textContent = newValue;
   611	
   612	          // Save to localStorage
   613	          saveTableData();
   614	
   615	          // Update summary table
   616	          updateSummaryTable();
   617	        }
   618	
   619	        input.addEventListener('blur', saveEdit);
   620	        input.addEventListener('keydown', function(e) {
   621	          if (e.key === 'Enter') {
   622	            input.blur();
   623	          } else if (e.key === 'Escape') {
   624	            cell.textContent = currentValue; // Revert
   625	          }
   626	        });
   627	      });
   628	    }
   629	
   630	    function parseLargeTextData() {
   631	      const largeTextValue = document.getElementById('largeTextBox').value;
   632	
   633	      if (largeTextValue.trim() === '') {
   634	        document.getElementById('textbox1').value = '';
   635	        document.getElementById('numberSelect').value = '0';
   636	        return;
   637	      }
   638	
   639	      // Parse logic: textbox1 gets everything from start to "tắm"
   640	      let textbox1Value = '';
   641	      let numberValue = '0';
   642	
   643	      // Extract everything from start to "tắm" for textbox1
   644	      // Convert to lowercase for searching "tắm"
   645	      const lowerCaseText = largeTextValue.toLowerCase();
   646	      const tamIndex = lowerCaseText.indexOf('tắm');
   647	      if (tamIndex !== -1) {
   648	        textbox1Value = largeTextValue.substring(0, tamIndex).trim();
   649	
   650	        // Extract number using pattern "tắm .*(\d+)b" for number select
   651	        const afterTam = largeTextValue.substring(tamIndex);
   652	        // Look for pattern "tắm.*(\d+)b" (case insensitive, flexible spacing)
   653	        const numberMatch = afterTam.match(/tắm\s*.*?(\d+)b/i);
   654	        if (numberMatch) {
   655	          const extractedNumber = parseInt(numberMatch[1]);
   656	          // Make sure the number is within the valid range (0-100)
   657	          if (extractedNumber >= 0 && extractedNumber <= 100) {
   658	            numberValue = extractedNumber.toString();
